import React, { Component } from 'react';

import Total from './Total'

class Statistics extends Component {

    state = {
        good: 0,
        neutral: 0,
        bad: 0
    }

    heandleGoodFeedback = () => {
        this.setState(prevState => ({
            good: prevState.good + 1,
        }))

    }
    heandleNeutralFeedback = () => {
        this.setState(prevState => ({
            neutral: prevState.neutral + 1,
        }))
    }

    heandleBadFeedback = () => {
        this.setState(prevState => ({
            bad: prevState.bad + 1,
        }))
    }


    render() {
        const { good, neutral, bad } = this.state;

        return (
            <>
                <h2>Please leave feedback</h2>
                <button type="button" onClick={this.heandleGoodFeedback}>Good</button>
                <button type="button" onClick={this.heandleBadFeedback}>Bad </button>
                <button type="button" onClick={this.heandleNeutralFeedback}>Neutral</button>
                <Total good={good} neutral={neutral} bad={bad} />
            </>
        )
    }
}

export default Statistics;
