import styles from './Statistic.module.css'

const Statistics = ({ statistics, title, color }) => {
    // function randomColor() { return Math.random() * 255; };
    // function setRandomColor() {
    //     return (
    //         'background-color = rgb' + (randomColor(), randomColor(), randomColor())
    //     )
    // }
    let backgroundColor = () => `rgb(${randomColor()},${randomColor()}, ${randomColor()})`;
    function randomColor() { return Math.random() * 255; };
    console.log(backgroundColor)

    //const setRandomColor = () => Math.random() * 255;// backgroundColor: `rgb(${randomColor()},${randomColor()}, ${randomColor()})` 
    // function randomColor() { return Math.random() * 255; }

    return (
        <section className={styles.statistics}>
            { !!title && <h2 className={styles.title}>{title}</h2>}
            <ul className={styles.stat_list}>
                {statistics.map(el => (
                    <li className={styles.item} key={el.id} style={{ backgroundColor: backgroundColor() }}>
                        <span className={styles.label}>{el.label}</span>
                        <span className={styles.percentage}>{el.percentage}%</span>
                    </li>
                ))}
            </ul>
        </section >
    )
}

export default Statistics;
