import styles from './Friends.module.css'
import greenDot from '../image/online.png'
import redDot from '../image/offline.png'
const FriendList = ({ friends }) => {

    return (
        <ul className={styles.friend_list}>
            { friends.map(el => (
                <li className={styles.item} key={el.id}>
                    <span className={styles.status}>{el.isOnline ? <img src={greenDot} width="15px" alt="online pic" /> : <img src={redDot} width="15px" alt="offline pic" />}</span>
                    <img className={styles.avatar} src={el.avatar} alt="pic" width="70px" />
                    <p className={styles.name}>{el.name}</p>
                </li>))
            }
        </ul>
    )
}


FriendList.defaultProps = {
    avatar:
        'https://dummyimage.com/640x480/2a2a2a/ffffff&text=Product+image+placeholder',

    name: 'uset',
};

export default FriendList
