import React, { Component } from 'react'



class ContactForm extends Component {

    state = {
        name: '',
        number: '',
    }

    heandleName = ev => {
        const newValue = ev.currentTurget.value;
        const turget = ev.currentTurget.name;
        this.setState({
            [turget]: newValue,
        })
        console.log(this.state.name)
    }

    heandleNumber = ev => {
        const numberValue = ev.target.value
        this.setState({
            number: numberValue
        })
        console.log("number", this.state.number)
    }

    render(onInputContact) {
        return (
            <form action="form">
                <label >Name
                <input type="text" name="name" onChange={this.heandleName} />
                </label>
                <label >Number
                <input type="phone" name="number" onChange={this.heandleNumber} />
                </label>
                <button type="button" onClick={onInputContact}>Add contact</button>
            </form>
        )
    }

}

export default ContactForm;
