import styles from './Transaction.module.css'

const TransactionHistory = ({ items }) => {
    function isOdd(num) {
        let number = num % 2;
        if (number === 0) {
            return 'white'
        }
        return 'rgb(196, 208, 235)'
    };


    return (
        <table className={styles.transactionHistory}>
            <thead className={styles.thead}>
                <tr className={styles.tr}>
                    <th className={styles.th}>Type</th>
                    <th className={styles.th}>Amount</th>
                    <th className={styles.th}>Currency</th>
                </tr>
            </thead>

            <tbody>
                {items.map((transaction, index) => (
                    <tr key={transaction.id} style={{ backgroundColor: isOdd(index) }}>
                        <td className={styles.td}>{transaction.type}</td>
                        <td className={styles.td}>{transaction.amount}</td>
                        <td className={styles.td}>{transaction.currency}</td>
                    </tr>
                ))}
            </tbody>
        </table >
    )
}

export default TransactionHistory;
