import React, { Component } from 'react';

import Feedback from './Feedback'
import Total from './Total'

class Statistics extends Component {

    state = {
        good: 0,
        neutral: 0,
        bad: 0,
        total: 0,
        persentage: 0
    }

    countPositiveFeedbackPercentage = (num, per, persentage) => {
        this.setState(prevState => ({
            persentage: (num * 100) / per
        }))
        console.log("persent", persentage)
    }

    countTotalFeedback = (good, neutral, bad, total, persentage) => {
        this.setState(prevState => ({
            total: prevState.total + (good + bad + neutral)
        }))
        this.countPositiveFeedbackPercentage(good, total, persentage)

    }

    heandleGoodFeedback = ({ good, neutral, bad, total }) => {
        console.log('this good', good)
        this.setState(prevState => ({
            good: prevState.good + 1,
            total: prevState.total + 1,
        }))
        this.countTotalFeedback(good, neutral, bad, total)
    }

    heandleNeutralFeedback = () => {
        this.setState(prevState => ({
            neutral: prevState.neutral + 1,
            total: prevState.total + 1,

        }))
    }
    heandleBadFeedback = () => {
        this.setState(prevState => ({
            bad: prevState.bad + 1,
            total: prevState.total + 1,

        }))
    }





    render() {
        const { good, neutral, bad, total, persentage } = this.state;

        return (
            <>
                <Feedback
                    heandleGoodFeedback={() => { this.heandleGoodFeedback({ good, neutral, bad, total, persentage }) }}
                    heandleBadFeedback={this.heandleBadFeedback}
                    heandleNeutralFeedback={this.heandleNeutralFeedback}
                />
                <Total good={good} neutral={neutral} bad={bad} total={total} positiveFeedback={persentage} />
            </>
        )
    }
}

export default Statistics;
