import React, { Component } from 'react'

import styles from './styles/PhoneBook.module.css'

const generateUniqueId = require('generate-unique-id');


class ContactForm extends Component {

    state = {
        id: generateUniqueId(),
        name: '',
        number: '',
    }

    handleNewValue = ev => {
        const newValue = ev.currentTarget.value;
        const target = ev.currentTarget.name;

        console.log('newValut', newValue)
        console.log('target', target)

        // this.setState({
        //     [target]: newValue,
        // })

        console.log(this.state)
    }

    handelData = ev => {
        ev.preventDefault();
        this.props.handleSubmit(this.state);
        console.log(this.state)

        // this.setState({
        //     id: generateUniqueId(),
        //     name: '',
        //     number: ''
        // })
    }

    render() {
        const inputNames = this.props.options;

        return (
            <form className={styles.form}>
                {inputNames.map(inputName => (
                    <label key={inputName} className={styles.label}>
                        {inputName}
                        <input type='text' key={inputName} name={inputName} value={this.state[inputName]} onChange={this.handleNewValue} className={styles.input} />
                    </label>
                ))}
                <button onClick={this.handelData} disabled={!!this.state.name || !!this.state.number ? true : false} className={styles.btn}>Add contact</button>
            </form >
        )
    }

}

export default ContactForm;
