import React, { Component } from 'react'

const generateUniqueId = require('generate-unique-id');



class ContactForm extends Component {

    state = {
        id: '',
        name: '',
        number: '',
    }


    heandleNewValue = ev => {
        const newValue = ev.currentTarget.value;
        const turget = ev.currentTarget.name;
        this.setState({
            [turget]: newValue,
            id: generateUniqueId()
        })
    }

    heandelData = ev => {
        ev.preventDefault();
        this.props.onSubmit(this.state);
        this.setState({
            id: '',
            name: '',
            number: ''
        })
    }

    render() {
        const inputNames = this.props.options;

        return (
            <form action="form" type="submit" onSubmit={this.heandelData}>
                {inputNames.map(inputName => (
                    <label>{inputName}
                        <input type='text' key={inputName} name={inputName} value={this.state[inputName]} onChange={this.heandleNewValue} />
                    </label>
                ))}
                <button type="submit">Add contact</button>
            </form>
        )
    }

}

export default ContactForm;
