import React, { Component } from 'react'
import generateUniqueId from 'generate-unique-id'


import styles from './styles/PhoneBook.module.css'

class ContactForm extends Component {

    state = {
        name: '',
        number: '',
        id: '',
    }

    handleNewValue = ev => {
        const { value, name } = ev.target;

        this.setState({
            [name]: value,
        })
    }

    handleNewContactData = () => {
        const newContact = this.state;
        const contacts = this.props.contacts;
        const checkedName = contacts.find(el => el.name.toLowerCase() === newContact.name.toLowerCase())

        const addId = () => this.setState({ id: generateUniqueId() })
        const sendContactToServer = () => this.props.handleSubmit(this.state);
        const resetState = () => this.setState({
            name: '', number: '', id: '',
        })

        if (!checkedName) {
            addId();
            sendContactToServer();
        } else { alert(`${newContact.name} already in contacts`) }

        resetState();
    }

    render() {
        const inputNames = this.props.options;
        console.log('props =', this.props.contacts)

        return (
            <form className={styles.form}>
                {inputNames.map(inputName => (
                    <label key={inputName} className={styles.label}>
                        {inputName}
                        <input type='text' key={inputName} name={inputName} value={this.state[inputName]} onChange={this.handleNewValue} className={styles.input} />
                    </label>
                ))}
                <button onClick={this.handleNewContactData} disabled={!this.state.name || !this.state.number} className={styles.btn}>Add contact</button>
            </form >
        )
    }

}

export default ContactForm;
