import React, { Component } from 'react'



class ContactForm extends Component {

    state = {
        name: '',
        number: '',
    }

    heandleName = ev => {
        const nameValue = ev.target.value
        this.setState({
            name: nameValue
        })
        console.log(this.state.name)
    }

    heandleNumber = ev => {
        const numberValue = ev.target.value
        this.setState({
            number: numberValue
        })
        console.log("number", this.state.number)
    }

    render(onInputContact) {
        return (
            <form action="form">
                <label >Name
                <input type="text" onChange={this.heandleName} />
                </label>
                <input type="phone" onChange={this.heandleNumber} />
                <label >Number </label>
                <button type="button" onClick={onInputContact}>Add contact</button>
            </form>
        )
    }

}

export default ContactForm;
