import React, { Component } from 'react'

const generateUniqueId = require('generate-unique-id');



class ContactForm extends Component {

    state = {
        name: '',
        number: '',
    }


    heandleNewValue = ev => {
        const newValue = ev.currentTarget.value;
        const turget = ev.currentTarget.name;
        this.setState({
            [turget]: newValue,
        })
        console.log(this.state[turget])

    }

    heandelData = ev => {
        this.props.onSubmit(this.state);
    }

    render() {
        const inputNames = this.props.options;
        const id = generateUniqueId();

        console.log(inputNames)

        return (
            <form action="form" type="submit" onClick={this.heandelData}>
                {inputNames.map(inputName => (
                    <label key={id}>{inputName}
                        <input type="" name={inputName} onChange={this.heandleNewValue} />
                    </label>
                ))}
                {/* <label >Name
                <input type="text" name="name" onChange={this.heandleNewValue} />
                </label>
                <label >Number
                <input type="phone" name="number" onChange={this.heandleNewValue} />
                </label> */}
                <button type="button">Add contact</button>
            </form>
        )
    }

}

export default ContactForm;
