import styles from './Profile.module.css'
import PropTypes from 'prop-types';

const defaultImg =
    "https://media.istockphoto.com/vectors/smile-vector-template-design-vector-id988112594?k=6&m=988112594&s=170667a&w=0&h=zy2D23dg7Hfz-A5pvz2BMiHi6Og2ed-DCldz367yvf0=";

const Profile = ({ user }) => {
    const { avatar, name, tag, location, stats } = user;
    console.log(avatar, name, tag)
    return (
        <div className={styles.profile}>
            <div className={styles.description}>
                <img
                    src={avatar}
                    alt="Аватар пользователя"
                    className={styles.avatar}
                />
                <p className={styles.name}>{name}</p>
                <p className={styles.tag}>@{tag}</p>
                <p className={styles.location}>{location}</p>
            </div>

            <ul className={styles.stats}>
                <li className={styles.list}>
                    <span className={styles.label}>Followers</span>
                    <span className={styles.quantity}>{stats.followers}</span>
                </li>
                <li className={styles.list} >
                    <span className={styles.label}>Views</span>
                    <span className={styles.quantity}>{stats.views}</span>
                </li>
                <li className={styles.list}>
                    <span className={styles.label}>Likes</span>
                    <span className={styles.quantity}>{stats.likes}</span>
                </li>
            </ul>
        </div >
    )
}

Profile.defaultProps = {
    avatar: defaultImg,
    name: 'Вася Тряпочкин',
}


Profile.propTypes = {
    user: {
        avatar: PropTypes.string,
        tag: PropTypes.string,
        location: PropTypes.string,
        followers: PropTypes.string,
        views: PropTypes.number,
        likes: PropTypes.number,
    }

};

export default Profile;
