import React, { Component } from 'react'

import styles from './components/styles/PhoneBook.module.css'

import './App.css';

import ContactForm from "./components/ContactForm";
import ContactList from "./components/ContactList";
import Filter from './components/Filter';


class App extends Component {

  state = {
    contacts: [

    ],
    filter: '',

  }

  handleData = (data) => {
    this.setState(prevState => ({
      contacts: [data, ...prevState.contacts]
    }))
  }

  hanbleFilterValue = (value) => {
    const contacts = this.state.contacts
    const filteredContacts = contacts.filter(contact => (contact.name.toLowerCase().includes(value.toLowerCase())));
    this.setState({ filter: filteredContacts })
  }


  render() {
    const { contacts } = this.state


    return (
      <div className={styles.container}>
        <h1>Phonebook</h1>
        <ContactForm options={['name', 'number']} onSubmit={this.handleData} />

        <h2>Contacts </h2>
        <Filter onCnange={this.hanbleFilterValue} />
        <ContactList contactsList={contacts} />
      </div>
    );
  }
}

export default App;
